package jadx.tests.integration.enums;

import jadx.tests.api.IntegrationTest;
import jadx.tests.api.utils.JadxMatchers;
import org.junit.Assert;
import org.junit.Test;

public class TestSwitchOverEnum extends IntegrationTest {

    public enum Count {
        ONE,
        TWO,
        THREE
    }

    public int testEnum(Count c) {
        switch (c) {
            case ONE:
                return 1;
            case TWO:
                return 2;
            default:
                return 0;
        }
    }

    public void check() {
        Assert.assertEquals(1, (long) testEnum(Count.ONE));
        Assert.assertEquals(2, (long) testEnum(Count.TWO));
        Assert.assertEquals(0, (long) testEnum(Count.THREE));
    }

    @Test
    public void test() {
        String code = getClassNode(TestSwitchOverEnum.class).getCode().toString();
        Assert.assertThat(code, JadxMatchers.countString(1, "synthetic"));
        Assert.assertThat(code, JadxMatchers.countString(2, "switch (c) {"));
        Assert.assertThat(code, JadxMatchers.countString(2, "case ONE:"));
    }
}
