import org.junit.Assert;
import org.junit.Test;

public class TestSuperInvoke extends IntegrationTest {

    public class A {
        public int a() {
            return 1;
        }
    }

    public class B extends A {
        public B() {
            super();
        }

        public int a() {
            return super.a() + 2;
        }

        public int test() {
            return a();
        }
