package jadx.tests.integration.loops;

import java.util.Arrays;
import java.util.List;
import org.hamcrest.Matchers;
import org.junit.Assert;

public class TestBreakInComplexIf2$TestCls {
    private int test(List<String> list) {
        int length = 0;
        for (String str : list) {
            if (!str.isEmpty() && str.length() <= 4) {
                if (!str.equals("skip")) {
                    if (str.equals("a")) {
                        break;
                    }
                    length++;
                }
            } else {
                break;
            }
        }
        return length;
    }

    public void check() {
        Assert.assertThat(Integer.valueOf(test(Arrays.asList(new String[]{"x", "y", "skip", "z", "a"}))), Matchers.is(Integer.valueOf(3)));
        Assert.assertThat(Integer.valueOf(test(Arrays.asList(new String[]{"x", "skip", ""}))), Matchers.is(Integer.valueOf(1)));
        Assert.assertThat(Integer.valueOf(test(Arrays.asList(new String[]{"skip", "y", "12345"}))), Matchers.is(Integer.valueOf(1)));
    }
}
